// +build dev

package swagger

import (
	"go/build"
	"net/http"
	"os"
	"strings"

	"github.com/shurcooL/httpfs/filter"
)

const path = "github.com/markphelps/flipt/swagger/api"

// Assets contains project assets.
var (
	p, _                   = build.Import(path, "", build.FindOnly)
	Assets http.FileSystem = filter.Skip(http.Dir(p.Dir), func(path string, fi os.FileInfo) bool {
		// ignore the autogenerated flipt.swagger.json in favor of the hand edited version
		return strings.HasSuffix(path, "/flipt.swagger.json")
	})
)
